mapper.insert:
INSERT INTO cm_board
(i_user, typ, sec_typ, seq, title, ctnt, board_img)
SELECT #{iUser}, #{typ}, #{secTyp}, IFNULL(MAX(seq), 0) + 1, #{title}, #{ctnt}, #{boardImg}
FROM cm_board
WHERE typ = #{typ} AND sec_typ = #{secTyp};

mapper.update:
UPDATE cm_board
SET title = #{title}
, ctnt = #{ctnt}
, board_img = #{boardImg}
WHERE i_board = #{iBoard}
AND i_user = #{iUser}

mapper.delete:
DELETE FROM cm_board
WHERE i_board = #{iBoard}
AND i_user = #{iUser}
