mapper.select:
SELECT A.i_product, A.product_typ, A.product_sectyp, A.product_seq, A.product_title, A.product_ctnt ,A.product_nm, A.price, FORMAT(A.sales/100,2) , final_price, A.product_dt, B.i_user, B.nm
FROM pd_board A LEFT JOIN t_user B ON A.i_user = B.i_user
WHERE A.i_product = #{iProduct}

mapper.update:
UPDATE pd_board
SET product_title = #{productTitle}, product_ctnt = #{productCtnt}, price = #{price}
, sales = #{sales}, final_price = #{price}-(#{price} * ((#{sales}/100))
WHERE i_product = #{iProduct}
AND i_user = #{iUser}

mapper.delete:
DELETE FROM pd_board
WHERE i_product = #{iProduct}
AND i_user = #{iUser}
