mapper.insert:
INSERT INTO pd_board
(	i_user
	, category
	, product_seq
	, product_title
	, product_ctnt
	, product_nm
	, brand
	, price
	, sales
	, final_price	)
VALUES
(	#{iUser}
	, #{category}
	, (SELECT IFNULL(MAX(product_seq), 0) + 1 FROM pd_board b WHERE category = #{category})
	, #{productTitle}
	, #{productCtnt}
	, #{productNm}
	, #{brand}
	, #{price}
	, #{sales}
	, #{price}-(#{price} * (#{sales}/100))	)

mapper.select:
SELECT A.i_product , A.category , A.product_title , A.product_nm , A.brand , A.price , A.sales , A.final_price , A.product_dt , A.i_user , B.nm , C.star_rt , IFNULL(C.review_cnt, 0) AS review_cnt , D.pd_img , IFNULL((SELECT COUNT(1) FROM pd_scrap E WHERE E.i_product = A.i_product AND E.i_user = #{iUser}), 0) AS scrap_chk FROM pd_board A
LEFT JOIN t_user B ON A.i_user = B.i_user
LEFT JOIN ( SELECT i_product , COUNT(i_product) AS review_cnt , star_rt FROM order_review GROUP BY i_product ) C ON A.i_product = C.i_product
LEFT JOIN ( SELECT * FROM pd_photo WHERE pdimg_seq = 1 ) D ON A.i_product = D.i_product
WHERE category = #{category}
ORDER BY sales DESC LIMIT #{sIdx}, #{recordCntPerPage}

mapper.update:
UPDATE pd_board
SET
	product_title = #{productTitle}
	, product_ctnt = #{productCtnt}
	, product_nm = #{productNm}
	, price = #{price}
	, sales = #{sales}
	, final_price = #{price}-(#{price} * ((#{sales}/100)))
WHERE i_product = #{iProduct} AND i_user = #{iUser}

mapper.delete:
DELETE FROM pd_board
WHERE i_product = #{iProduct}
AND i_user = #{iUser}

mapper.select:
SELECT COUNT(i_product) 
FROM pd_scrap
WHERE i_product = #{iProduct} AND i_user = #{iUser}

mapper.update:
UPDATE cart
SET cart_count = cart_count + 1
WHERE i_product = #{iProduct} AND i_user = #{iUser}
