set
	nm = #{nm}
	,gender=#{gender}


		,ph = #{ph}

	,profile_img = #{profileImg}

where i_user=#{iUser}

update t_user
set
	user_pw = #{userPw}
where
	i_user=#{iUser}

mapper.select:
SELECT email_id
FROM t_user
WHERE email_id = #{emailId}

SELECT nm
FROM t_user
